public class Q1044 {

    public String longestDupSubstringWithoutHash(String s) {
        Set<String> set = new HashSet<>();
        String str, res = "";
        int l = 1, r = s.length(), m;
        boolean flag = false;

        while (l < r) {
            m = l + (r - l) / 2;

            flag = false;
            set.clear();
            for (int i = 0; i <= s.length() - m; i++) {
                str = s.substring(i, i + m);
                if (set.contains(str)) {
                    flag = true;
                    res = str;
                    break;
                } else {
                    set.add(str);
                }
            }

            if (flag) {
                l = m + 1;
            } else {
                r = m;
            }
        }

        return res;
    }

    // fail to solve
    public String longestDupSubstring(String s) {
        Set<Integer> set = new HashSet<>();
        String str, res = "";
        int l = 1, r = s.length(), m;
        boolean flag = false;

        while (l < r) {
            m = l + (r - l) / 2;

            flag = false;
            set.clear();
            for (int i = 0; i <= s.length() - m; i++) {
                str = s.substring(i, i + m);
                if (set.contains(str.hashCode())) {
                    flag = true;
                    res = str;
                    break;
                } else {
                    set.add(str.hashCode());
                }
            }

            if (flag) {
                l = m + 1;
            } else {
                r = m;
            }
        }

        return res;
    }

    public static void main(String[] args) {
        System.out.println(new Q1044().longestDupSubstring("aa"));
        System.out.println(new Q1044().longestDupSubstring("abcd"));
        System.out.println(new Q1044().longestDupSubstring("abbaba"));
        System.out.println(new Q1044().longestDupSubstring("aaaaa"));
        System.out.println(new Q1044().longestDupSubstring(""));
    }

}
