public class Q1062 {

    public int longestRepeatingSubstring(String s) {
        Set<String> set = new HashSet<>();
        String str;
        int len = s.length() - 1;

        while (len > 0) {
            set.clear();
            for (int i = 0; i <= s.length() - len; i++) {
                str = s.substring(i, i + len);
                if (set.contains(str)){
                     return len;
                } else {
                    set.add(str);
                }
            }
            len--;
        }

        return 0;
    }

    public static void main(String[] args) {
        System.out.println(new Q1062().longestRepeatingSubstring("abcd"));
        System.out.println(new Q1062().longestRepeatingSubstring("abbaba"));
        System.out.println(new Q1062().longestRepeatingSubstring("aaaaa"));
        System.out.println(new Q1062().longestRepeatingSubstring(""));
    }

}
